import React, { useState, useEffect } from "react";
import "../css/BreathingExercise.css"; // Import your CSS file

const BreathingExercise = () => {
  const [breathsLeft, setBreathsLeft] = useState(3);
  const [isButtonDisabled, setButtonDisabled] = useState(false);

  const handleBreathChange = (event) => {
    setBreathsLeft(parseInt(event.target.value, 10));
  };

  const growCircle = () => {
    const circleProgress = document.querySelector(".circle-progress");
    circleProgress.classList.add("circle-grow");
    setTimeout(() => {
      circleProgress.classList.remove("circle-grow");
    }, 5000);
  };

  const breathTextUpdate = () => {
    setBreathsLeft((prevBreaths) => prevBreaths - 1);
    // Update other state or DOM elements as needed
  };

  const breathingApp = () => {
    const breathingAnimation = setInterval(() => {
      if (breathsLeft === 0) {
        clearInterval(breathingAnimation);
        // Handle end of breathing session
        setButtonDisabled(false);
        return;
      }
      growCircle();
      breathTextUpdate();
    }, 12000);
  };

  const startBreathing = () => {
    setButtonDisabled(true);
    setBreathsLeft(parseInt(document.querySelector(".breath-input").value, 10));
    breathTextUpdate();
    growCircle();
    breathingApp();
  };

  useEffect(() => {
    if (breathsLeft === 0) {
      setButtonDisabled(false);
      setBreathsLeft(parseInt(document.querySelector(".breath-input").value, 10));
      return;
    }
    const breathingAnimation = setInterval(() => {
      growCircle();
      breathTextUpdate();
    }, 12000);

    return () => clearInterval(breathingAnimation);
  }, [breathsLeft]);

  return (
    <div className="App">
      <div className="input">
        <label>Select Breaths</label>
        <select className="breath-input" onChange={handleBreathChange} value={breathsLeft}>
          <option value="3">3 Breaths</option>
          <option value="5">5 Breaths</option>
          <option value="7">7 Breaths</option>
        </select>
      </div>
      <div className="circle-wrap">
        <div className="circle-outline"></div>
        <div className="circle-progress"></div>
      </div>
      <p className="breaths">Breaths remaining: <span className="breaths-text">{breathsLeft}</span></p>
      <p className="instructions">Are you ready to start breathing?</p>
      <button className={`start ${isButtonDisabled ? "button-inactive" : ""}`} onClick={startBreathing}>
        Begin
      </button>
    </div>
  );
};

export default BreathingExercise;
