import React, { useState, useEffect, useContext } from 'react';
import AuthContext from '../context/AuthContext'

const RegisterForm = () => {

  let {authTokens, user} = useContext(AuthContext);


  const [branches, setBranches] = useState([]);


  const [selectedAppointmentType, setSelectedAppointmentType] = useState(null);

  const handleAppointmentTypeChange = (appointmentType) => {
    setSelectedAppointmentType(appointmentType);
  };

  const [selectedBranch, setSelectedBranch] = useState(null);

  useEffect(() => {
    fetch('http://127.0.0.1:8000/api/clinic/branches', {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${authTokens.access}`,
        'Content-Type': 'application/json',
      },
    })
      .then(res => res.json())
      .then(data => {
        setBranches(data);
        // Select the first branch by default
        if (data.length > 0) {
          setSelectedBranch(data[0].id);
        }
      });
  }, []);

  const handleBranchClick = (branchId) => {
    setSelectedBranch(branchId);
    setSelectedDoctor(null)
  };

    const [doctors, setDoctors] = useState([]);

    useEffect(() => {
      if (selectedBranch) {
        fetch('http://localhost:8000/api/clinic/doctors', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${authTokens.access}`,
            'Content-type': 'application/json'
          }
        })
          .then(response => response.json())
          .then(data => {
            // Filter the data based on the selected branch
            const filteredData = data.filter(doctor => doctor.branch_name === selectedBranch);
            setDoctors(filteredData);
          })
          .catch(error => console.error('Error fetching doctors:', error));
      }
    }, [selectedBranch]);


    const [appointments, setAppointments] = useState([]);

    useEffect(() => {
        // Function to fetch appointments
        const fetchAppointments = async () => {
          try {
            const response = await fetch('http://localhost:8000/api/clinic/appointments', {
              method: 'GET',
              headers: {
                'Authorization': `Bearer ${authTokens.access}`,
                'Content-type': 'application/json',
              }
            });
    
            if (response.ok) {
              const data = await response.json();
              setAppointments(data);
            } else {
              console.error('Error fetching appointments:', response.statusText);
            }
          } catch (error) {
            console.error('Error fetching appointments:', error);
          }
        };
    
        // Fetch appointments initially
        fetchAppointments();
    
        // Set up polling with a 5-second interval
        const pollingInterval = setInterval(() => {
          fetchAppointments();
        }, 5000);
    
        // Clear the interval when the component unmounts
        return () => clearInterval(pollingInterval);
    
      }, [authTokens.access]);

    const hasExistingScheduleForToday = async () => {
        try {
          const today = new Date().toISOString().split('T')[0];
          const response = await fetch('http://localhost:8000/api/clinic/clients', {
            method: 'GET',
            headers: {
              'Authorization': `Bearer ${authTokens.access}`,
              'Content-type': 'application/json',
            }
          });
    
          if (response.ok) {
            const data = await response.json();
            return data.some(
              (client) =>
                client.userID === user.user_id &&
                client.appointment_date === today
            );
          } else {
            console.error('Error fetching clients:', response.statusText);
            return false;
          }
        } catch (error) {
          console.error('Error checking existing schedule:', error);
          return false;
        }
      };

  // FUNCTIONS FOR ADDING CLIENTS

  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    age: '',
    birthday: '',
    contact_number: '',
    emergency_contact_number: '',
    email: '',
    doctor: '',
    appointment_date: new Date().toISOString().split('T')[0],
    appointment_time: '',
    medical_history: '',
    status: 'active',
    branch_id: '', 
    userID: null, 
  });

  useEffect(() => {
    setFormData({
      ...formData,
      branch_id: selectedBranch,
      userID: user.user_id
    })
  }, [selectedBranch])

  const [showConfirmation, setShowConfirmation] = useState(false);

  const handleClientFormChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };


  const updateAppointmentTime = async (appointmentId, timeField, currentValue) => {
    try {
      const response = await fetch(`http://localhost:8000/update-appointment/${appointmentId}/`, {
        method: 'PATCH', // Assuming you use a PATCH request to update the time field
        headers: {
          'Authorization': `Bearer ${authTokens.access}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ [timeField]: !currentValue }), // Send the updated time field as false
      });

      if (response.ok) {
        // Successfully updated the appointment time field
        // You may want to refresh the data or handle it in a way that suits your application
        console.log(`Successfully updated ${timeField} for appointment ${appointmentId}`);
        fetch('http://localhost:8000/api/clinic/appointments', {
        method: 'GET',
        headers: {
            'Authorization': `Bearer ${authTokens.access}`,
            'Content-type': 'application/json'
        }

        })
          .then(response => response.json())
          .then(data => {
            setAppointments(data)
          })
          .catch(error => console.error('Error fetching appointments:', error));

      } else {
        // Handle the error if the update request fails
        console.error('Failed to update appointment time field');
      }
    } catch (error) {
      console.error('Error updating appointment time field:', error);
    }
  };

  const handleClientFormSubmit = async (e) => {
    e.preventDefault();

    // Display confirmation dialog
    const userConfirmed = window.confirm('Do you want to submit the form?');
    const existingScheduleForToday = await hasExistingScheduleForToday();

    if (userConfirmed) {
      console.log(formData);
      setShowConfirmation(true);

      if (existingScheduleForToday){
        window.alert('gago')
      } else{

        try{

            const response = await fetch('http://localhost:8000/api/clinic/clients/create', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${authTokens.access}`,
              },
              body: JSON.stringify({
                first_name: formData.first_name.trim(),
                last_name: formData.last_name.trim(),
                age: formData.age.trim(),
                birthday: formData.birthday.trim(),
                contact_number: formData.contact_number.trim(),
                emergency_contact_number: formData.emergency_contact_number.trim(),
                email: formData.email.trim(),
                doctor: formData.doctor,
                appointment_date: formData.appointment_date.trim(),
                appointment_time: formData.appointment_time.trim(),
                medical_history: formData.medical_history.trim(),
                status: formData.status.trim(),
                branch: formData.branch_id,
                userID: formData.userID
              }),
            })
    
              if (response.ok) {
                const data = await response.json();
    
                // Reset the form data
                setFormData({
                  first_name: '',
                  last_name: '',
                  age: '',
                  birthday: '',
                  contact_number: '',
                  emergency_contact_number: '',
                  email: '',
                  doctor: '',
                  appointment_date: '',
                  appointment_time: '',
                  medical_history: '',
                });
    
                updateAppointmentTime(selectedAppointmentId, selectedTimeField, selectedCurrentValue);
         
                // Fetch the updated appointments if needed
                // (You can replace this with direct state manipulation if necessary)
                fetch('http://localhost:8000/api/clinic/appointments', {
                  method: 'GET',
                  headers: {
                    'Authorization': `Bearer ${authTokens.access}`,
                    'Content-type': 'application/json',
                  },
                })
                .then(response => response.json())
                .then(data => {
                  // Filter the data based on the 'branch' field
                  const filteredData = data.filter(appointment => appointment.branch === user.branch.id);
                  setAppointments(filteredData);
                })
                .catch(error => console.error('Error fetching appointments:', error));
              } else {
                // Handle the error if the create request fails
                console.error('Failed to create client:', response.statusText);
              }
    
          } catch (error) {
    
          }

      }

    } else {
      // Handle cancellation if needed
    }
  };

  // console.log('HAHHAAA: ', branchId)
  console.log('HAHAHAHAHA: ', formData)

  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [selectedAppointments, setSelectedAppointments] = useState([]);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
  const [selectedAppointmentId, setSelectedAppointmentId] = useState(null)
  const [selectedTimeField, setSelectedTimeField] = useState(null)
  const [selectedCurrentValue, setSelectedCurrentValue] = useState(null)

  //appointmentId, timeField, currentValue

  useEffect(() => {
    setFormData({
      ...formData,
      doctor: selectedDoctor ? selectedDoctor.id : '',
      appointment_time: selectedTimeSlot ? selectedTimeSlot : '',
    })
  }, [selectedDoctor, selectedTimeSlot])

  console.log("NAYAHA: ", formData)

  useEffect(() => {
    // Update selectedAppointments when selectedDoctor changes
    if (selectedDoctor) {
      const doctorAppointments = appointments.filter(
        (appointment) => appointment.doctor === selectedDoctor.id
      );
      setSelectedAppointments(doctorAppointments);
    }
  }, [selectedDoctor, doctors, appointments]); // Watch for changes in selectedDoctor

  const handleDoctorClick = (doctor) => {
    setSelectedDoctor(doctor);
  };

  const handleAppointmentTime = (timeSlot, appointmentId, timeField, currentValue) => {
    setSelectedTimeSlot(timeSlot);
    setSelectedAppointmentId(appointmentId);
    setSelectedTimeField(timeField);
    setSelectedCurrentValue(currentValue);
  }

  console.log(selectedAppointmentId, selectedCurrentValue, selectedTimeField)



  return (
    <div>
      <div className='add-client'>

        <div className='select-branch-buttons'>
            {branches.map((branch) => (
            <button
                key={branch.id}
                onClick={() => handleBranchClick(branch.id)}
                style={{ backgroundColor: selectedBranch === branch.id ? 'blue' : 'lightcoral' }}
            >
                {branch.branch_name}
            </button>
            ))}
        </div>

        <div>
            {doctors.map((doctor) => (
            <button key={doctor.id} onClick={() => handleDoctorClick(doctor)}
            style={{backgroundColor: selectedDoctor === doctor ? 'blue' : 'lightcoral'}}>
                {doctor.doctor_name}
            </button>
            ))}
        </div>

        {selectedDoctor && selectedAppointments.length > 0 && (
                <div>
                <h3>Appointments for {selectedDoctor.doctor_name}</h3>
                <ul>
                    {selectedAppointments.map((appointment) => (
                    <button disabled={appointment.time_8am_9am} style={{backgroundColor: appointment.time_8am_9am || selectedTimeSlot === '8am - 9am' ? 'red' : 'black'}} onClick={() => handleAppointmentTime('8am - 9am', appointment.id, 'time_8am_9am', appointment.time_8am_9am)}>8am - 9am</button>
                    ))}
                    {selectedAppointments.map((appointment) => (
                    <button disabled={appointment.time_9am_10am} style={{backgroundColor: appointment.time_9am_10am || selectedTimeSlot === '9am - 10am' ? 'red' : 'black'}} onClick={() => handleAppointmentTime('9am - 10am', appointment.id, 'time_9am_10am', appointment.time_9am_10am)}>9am - 10am</button>
                    ))}
                    {selectedAppointments.map((appointment) => (
                    <button disabled={appointment.time_10am_11am} style={{backgroundColor: appointment.time_10am_11am || selectedTimeSlot === '10am - 11am' ? 'red' : 'black'}} onClick={() => handleAppointmentTime('10am - 11am', appointment.id, 'time_10am_11am', appointment.time_10am_11am)}>10am - 11am</button>
                    ))}
                    {selectedAppointments.map((appointment) => (
                    <button disabled={appointment.time_11am_12pm} style={{backgroundColor: appointment.time_11am_12pm || selectedTimeSlot === '11am - 12pm' ? 'red' : 'black'}} onClick={() => handleAppointmentTime('11am - 12pm', appointment.id, 'time_11am_12pm', appointment.time_11am_12pm)}>11am - 12pm</button>
                    ))}
                    {selectedAppointments.map((appointment) => (
                    <button disabled={appointment.time_1pm_2pm} style={{backgroundColor: appointment.time_1pm_2pm || selectedTimeSlot === '1pm - 2pm' ? 'red' : 'black'}} onClick={() => handleAppointmentTime('1pm - 2pm', appointment.id, 'time_1pm_2pm', appointment.time_1pm_2pm)}>1pm - 2pm</button>
                    ))}
                    {selectedAppointments.map((appointment) => (
                    <button disabled={appointment.time_2pm_3pm} style={{backgroundColor: appointment.time_2pm_3pm || selectedTimeSlot === '2pm - 3pm' ? 'red' : 'black'}} onClick={() => handleAppointmentTime('2pm - 3pm', appointment.id, 'time_2pm_3pm', appointment.time_2pm_3pm)}>2pm - 3pm</button>
                    ))}
                    {selectedAppointments.map((appointment) => (
                    <button disabled={appointment.time_3pm_4pm} style={{backgroundColor: appointment.time_3pm_4pm || selectedTimeSlot === '3pm - 4pm' ? 'red' : 'black'}} onClick={() => handleAppointmentTime('3pm - 4pm', appointment.id, 'time_3pm_4pm', appointment.time_3pm_4pm)}>3pm - 4pm</button>
                    ))}
                    {selectedAppointments.map((appointment) => (
                    <button disabled={appointment.time_4pm_5pm} style={{backgroundColor: appointment.time_4pm_5pm || selectedTimeSlot === '4pm - 5pm' ? 'red' : 'black'}} onClick={() => handleAppointmentTime('4pm - 5pm', appointment.id, 'time_4pm_5pm', appointment.time_4pm_5pm)}>4pm - 5pm</button>
                    ))}
                </ul>
                </div>
            )}

        <form onSubmit={handleClientFormSubmit}>
        <label>
            First Name:
            <input
            type="text"
            name="first_name"
            value={formData.first_name}
            onChange={handleClientFormChange}
            />
        </label>

        <label>
            Last Name:
            <input
            type="text"
            name="last_name"
            value={formData.last_name}
            onChange={handleClientFormChange}
            />
        </label>

        <label>
            Age:
            <input
            type="text"
            name="age"
            value={formData.age}
            onChange={handleClientFormChange}
            />
        </label>

        <label>
            Birthday:
            <input
            type="text"
            name="birthday"
            value={formData.birthday}
            onChange={handleClientFormChange}
            />
        </label>

        <label>
            Contact Number:
            <input
            type="text"
            name="contact_number"
            value={formData.contact_number}
            onChange={handleClientFormChange}
            />
        </label>

        <label>
            Emergency Contact Number:
            <input
            type="text"
            name="emergency_contact_number"
            value={formData.emergency_contact_number}
            onChange={handleClientFormChange}
            />
        </label>

        <label>
            Email:
            <input
            type="text"
            name="email"
            value={formData.email}
            onChange={handleClientFormChange}
            />
        </label>

        <label>
            Medical History:
            <textarea
            name="medical_history"
            value={formData.medical_history}
            onChange={handleClientFormChange}
            />
        </label>

        {/* Add your branch_id and userID input fields here */}

        <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
};


export default RegisterForm;
