import React, { useEffect, useState } from 'react';
import '../css/BreathingExercise.css';

function BreathingExercise() {
  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    let breathInterval;

    const container = document.getElementById('container');
    const text = document.getElementById('text');

    const totalTime = 7500;
    const breatheTime = (totalTime / 5) * 2;

    const breathAnimation = () => {
      text.innerText = 'Breathe In!';
      container.className = 'container grow';

      setTimeout(() => {
        text.innerText = 'Hold';

        setTimeout(() => {
          text.innerText = 'Breathe Out!';
          container.className = 'container shrink';
        }, totalTime / 5);
      }, (totalTime / 5) * 2);
    };

    if (isRunning) {
      breathInterval = setInterval(breathAnimation, totalTime);
    }

    return () => clearInterval(breathInterval);
  }, [isRunning]);

  const startBreathing = () => {
    setIsRunning(true);
  };

  const stopBreathing = () => {
    setIsRunning(false);
  };

  return (
    <div className="breathing-app">
      <h1>Relaxer</h1>

      <div className="container" id="container">
        <div className="circle"></div>

        <p id="text"></p>

        <div className="pointer-container">
          <span className="pointer"></span>
        </div>

        <div className="gradient-circle"></div>
      </div>

      <div className="buttons-container">
        <button onClick={startBreathing} disabled={isRunning}>
          Start
        </button>
        <button onClick={stopBreathing} disabled={!isRunning}>
          Stop
        </button>
      </div>
    </div>
  );
}

export default BreathingExercise;
