import React, { useState, useRef } from 'react';
import '../css/BreathingExercise.css';

const BreathingExercise_child = () => {
  const [isRunning, setIsRunning] = useState(false);
  const [breathIn, setBreathIn] = useState(true);
  const [timer, setTimer] = useState(0);
  const circleRef = useRef(null);

  const startPauseHandler = () => {
    setIsRunning(!isRunning);
    if (!isRunning) {
      setBreathIn(true);
      setTimer(0);
      animateBreathing();
    }
  };

  const animateBreathing = () => {
    const maxBreathInTime = 4000; // in milliseconds
    const maxBreathOutTime = 4000; // in milliseconds
    const breathCycleTime = maxBreathInTime + maxBreathOutTime;

    const updateCircle = () => {
      const circle = circleRef.current;

      if (circle) {
        const breathTime = breathIn ? maxBreathInTime : maxBreathOutTime;
        const progress = (timer % breathCycleTime) / breathTime;

        const radius = 50 + 50 * Math.sin(progress * Math.PI);
        circle.setAttribute('r', radius.toString());
      }
    };

    const updateTimer = () => {
      setTimer((prevTimer) => prevTimer + 16); // assuming 60 fps
    };

    const animate = () => {
      updateTimer();
      updateCircle();

      if (timer < breathCycleTime && isRunning) {
        requestAnimationFrame(animate);
      } else {
        setIsRunning(false);
      }
    };

    animate();
  };

  return (
    <div className="breathing-exercise">
      <svg width="200" height="200">
        <circle ref={circleRef} cx="100" cy="100" r="50" fill="#87CEEB" />
      </svg>
      <button onClick={startPauseHandler}>{isRunning ? 'Pause' : 'Start'}</button>
    </div>
  );
};

function BreathingExercise() {
  return (
    <div className="App">
      <header className="App-header">
        <BreathingExercise_child />
      </header>
    </div>
  );
}

export default BreathingExercise;
