import React, { useState, useEffect } from "react";
import "../css/BreathingExercise.css";

const BreathingExercise = () => {

    const [breathsLeft, setBreathsLeft] = useState(3);
  const [isBreathing, setIsBreathing] = useState(false);

  const growCircle = () => {
    // Implement your circle animation logic here
    const circleProgress = document.querySelector(".circle-progress");
    circleProgress.classList.add("circle-grow");
    setTimeout(() => {
      circleProgress.classList.remove("circle-grow");
    }, 8000);
  };

  const breathTextUpdate = () => {
    setBreathsLeft((prevBreaths) => prevBreaths - 1);
    const breathsText = document.querySelector(".breaths-text");
    const instructions = document.querySelector(".instructions");

    breathsText.innerText = breathsLeft;
    instructions.innerText = "Breath in";

    setTimeout(() => {
      instructions.innerText = "Hold Breath";
      setTimeout(() => {
        instructions.innerText = "Exhale Breath Slowly";
      }, 4000);
    }, 4000);
  };

  const breathingApp = () => {
    const breathingAnimation = setInterval(() => {
      if (breathsLeft === 0) {
        clearInterval(breathingAnimation);
        setIsBreathing(false);
        const instructions = document.querySelector(".instructions");
        const startButton = document.querySelector(".start");

        instructions.innerText =
          "Breathing session completed. Click 'Begin' to start another session!";
        startButton.classList.remove("button-inactive");

        setBreathsLeft(parseInt(document.querySelector(".breath-input").value, 10));
        return;
      }
      growCircle();
      breathTextUpdate();
    }, 12000);
  };

  const startBreathing = () => {
    setIsBreathing(true);
    setBreathsLeft(parseInt(document.querySelector(".breath-input").value, 10));

    setTimeout(() => {
      const instructions = document.querySelector(".instructions");
      const startButton = document.querySelector(".start");

      instructions.innerText = "Get relaxed, and ready to begin breathing";

      setTimeout(() => {
        instructions.innerText = "Breathing is about to begin...";
        setTimeout(() => {
          breathingApp();
          growCircle();
          breathTextUpdate();
        }, 2000);
      }, 2000);
    });
  };

  useEffect(() => {
    if (!isBreathing) {
      setBreathsLeft(3);
    }
  }, [isBreathing]);

  return (
    <div className="App">
      <div className="circle-progress"></div>
      <input className="breath-input" type="number" />
      <button className={`start ${isBreathing ? 'button-inactive' : ''}`} onClick={startBreathing}>
        Begin
      </button>
      <div className="instructions"></div>
      <div className="breaths-text">{breathsLeft}</div>
    </div>
  );

};

export default BreathingExercise;
