import React, { useState, useEffect } from 'react';
import '../css/BreathingExercise.css';

function BreathingExercise() {
  const [timeRemaining, setTimeRemaining] = useState(60);
  const [isRunning, setIsRunning] = useState(false);
  const [instruction, setInstruction] = useState('');
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      if (isRunning && timeRemaining > 0) {
        setTimeRemaining(timeRemaining - 1);
        setProgress((progress + 100 / 60) % 100);
        setInstruction(timeRemaining % 4 === 0 ? 'Breathe in' : 'Breathe out');
      }
    }, 1000);

    return () => clearInterval(intervalId);
  }, [timeRemaining, isRunning]);

  const handleStartPause = () => {
    setIsRunning(!isRunning);
  };

  return (
    <div className="breathing-exercise">
      <div className="timer-container">
        <div className="timer" style={{ transform: `rotate(${progress * 3.6}deg)` }} />
      </div>
      <div className="instruction">{instruction}</div>
      <button className="start-pause-button" onClick={handleStartPause}>
        {isRunning ? 'Pause' : 'Start'}
      </button>
    </div>
  );
}

export default BreathingExercise;
