// BreathingExercise.js

import React, { useState, useEffect } from 'react';
import '../css/BreathingExercise.css';

function BreathingExercise() {
  const [breathing, setBreathing] = useState(false);
  const [pointerRotation, setPointerRotation] = useState('');

  useEffect(() => {
    const container = document.getElementById('container');
    const text = document.getElementById('text');

    const totalTime = 7500;
    const breatheTime = (totalTime / 5) * 2;
    const holdTime = totalTime / 5;

    let breathInterval;

    const breathAnimation = () => {
      text.innerText = 'Breathe In!';
      container.classList.add('grow');
      setPointerRotation('rotate 7.5s linear infinite');

      setTimeout(() => {
        text.innerText = 'Hold';

        setTimeout(() => {
          text.innerText = 'Breathe Out!';
          container.classList.add('shrink');
          setPointerRotation('');
        }, holdTime);
      }, breatheTime);
    };

    if (breathing) {
      breathInterval = setInterval(breathAnimation, totalTime);
    }

    return () => {
      clearInterval(breathInterval);
    };
  }, [breathing]);

  const startBreathing = () => {
    setBreathing(true);
  };

  const stopBreathing = () => {
    setBreathing(false);
  };

  return (
    <div className="breathing-app">
      <h1>Relaxer</h1>
      <button onClick={startBreathing}>Start</button>
      <button onClick={stopBreathing}>Stop</button>

      <div className="container" id="container">
        <div className="circle"></div>

        <p id="text"></p>

        <div className="pointer-container">
          <span className="pointer" style={{ animation: pointerRotation }}></span>
        </div>

        <div className="gradient-circle"></div>
      </div>
    </div>
  );
}

export default BreathingExercise;
