import React, { useState, useEffect } from 'react';
import '../css/BreathingExercise.css'

const BreathingExercise = () => {
    const [breathsLeft, setBreathsLeft] = useState(3);
    const [isGrowing, setIsGrowing] = useState(false);
    const [isInactive, setIsInactive] = useState(false);
  
    const breathTextUpdate = () => {
      setBreathsLeft(prevBreaths => prevBreaths - 1);
    };
  
    const growCircle = () => {
      setIsGrowing(true);
      setTimeout(() => {
        setIsGrowing(false);
      }, 8000);
    };
  
    const breathingApp = () => {
      const breathingAnimation = setInterval(() => {
        if (breathsLeft === 0) {
          clearInterval(breathingAnimation);
          setBreathsLeft(3);
          setIsInactive(false);
          return;
        }
        growCircle();
        breathTextUpdate();
      }, 12000);
    };
  
    const handleBreathChange = (e) => {
      setBreathsLeft(parseInt(e.target.value, 10));
    };
  
    const handleStartClick = () => {
      setIsInactive(true);
      breathingApp();
      growCircle();
      breathTextUpdate();
    };
  
    return (
      <div>
        <BreathSelector onChange={handleBreathChange} />
        <Circle isGrowing={isGrowing} />
        <BreathCounter breathsLeft={breathsLeft} />
        <Instructions text={isInactive ? "Breathing session completed. Click 'Begin' to start another session!" : "Get relaxed, and ready to begin breathing"} />
        <StartButton onClick={handleStartClick} isInactive={isInactive} />
      </div>
    );
  };
  
  export default BreathingExercise;

  export const BreathSelector = ({ onChange }) => {
    return (
      <div className="input">
        <label>Select Breaths</label>
        <select className="breath-input" onChange={onChange}>
          <option value="3">3 Breaths</option>
          <option value="5">5 Breaths</option>
          <option value="7">7 Breaths</option>
        </select>
      </div>
    );
  };

  export const Circle = ({ isGrowing }) => {
    return (
      <div className="circle-wrap">
        <div className={`circle-outline ${isGrowing ? 'circle-grow' : ''}`}></div>
        <div className="circle-progress"></div>
      </div>
    );
  };

  export const BreathCounter = ({ breathsLeft }) => {
    return (
      <p className="breaths">Breaths remaining: <span className="breaths-text">{breathsLeft}</span></p>
    );
  };

  export const Instructions = ({ text }) => {
    return (
      <p className="instructions">{text}</p>
    );
  };
  
  
  export const StartButton = ({ onClick, isInactive }) => {
    return (
      <button className={`start ${isInactive ? 'button-inactive' : ''}`} onClick={onClick}>Begin</button>
    );
  };
