import React, { useState, useRef, useEffect } from 'react';
import '../css/assessment.css';
import Nav from './nav';


export default function Assessment() {
    return (
        <body>
            <Nav color = 'black' page = 'assessment' rIcon = 'white'/>
            <Assessment_main />
        </body>
    );
}

function Assessment_main (){

    const [activeQuestion, setActiveQuestion] = useState(0)
    console.log(activeQuestion)

    const { questions } = quiz
    const { question, choices } = questions[activeQuestion]

    const [selectedChoices, setSelectedChoices] = useState({
        choice1: null,
        choice2: null,
        choice3: null,
        choice4: null,
    });
    
    const [quiz1Score, setQuiz1Score] = useState(null);

    const selectedChoiceCount = Object.values(selectedChoices).filter(choice => choice !== null).length;

    const onClickNext = () => {
        setActiveQuestion((prev) => prev + 1)
        

    }

    const onClickFinish = () => {
        let totalScore =
            parseInt(selectedChoices.choice1) +
            parseInt(selectedChoices.choice2) +
            parseInt(selectedChoices.choice3) +
            parseInt(selectedChoices.choice4);

        setActiveQuestion(0)
        setSelectedChoices({
            choice1: null,
            choice2: null,
            choice3: null,
            choice4: null,
        });


        setQuiz1Score(totalScore);
        
        if (totalScore === 10 && totalScore <= 15){
            alert('1')
        }
        if (totalScore === 16 && totalScore <= 20){
            alert('2')
        }
        if (totalScore === 21 && totalScore <= 25){
            alert('3')
        }

    }

    const onChoiceClick = (choice) => {
        let num = choices.indexOf(choice) + 2;
        // Update the selected choice for the current question
        setSelectedChoices((prevChoices) => ({
            ...prevChoices,
            [`choice${activeQuestion + 1}`]: num,
        }));
        console.log('index: ', num)
    };

    const [openBg, setOpenBg] = useState(false);
    const[openQuiz1, setOpenQuiz1] = useState(true);
    const[openQuiz2, setOpenQuiz2] = useState(false);
    const[openQuiz3, setOpenQuiz3] = useState(false);

    const[openAssessment, setOpenAssessment] = useState(false);

    const handleBgClose = () => {
        setOpenQuiz1(false);
        setOpenQuiz2(false);
        setOpenQuiz3(false);

    }
    
    const handleQuiz1Close = () => {
        setOpenQuiz1(false)
    }

    return(
        <div>
            <div class="bg-img-asses">
                <img src="img/asses-bg.png" />
            </div>
            <div class="hero-asses">
                <div class="hero-img-asses">
                    <img src="img/test.png" />
                </div>
                <div class="hero-cntxt-asses">
                    <h1>Lorem, ipsum dolor.</h1>
                    <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolorum asperiores minus ipsa consequatur molestiae fugiat dolor maxime quaerat, nemo, porro, ex ea nulla! Adipisci, impedit. Pariatur aut est cupiditate facilis.</p>
                    <a class = "btn-mn" href = "#">Try me</a>
                </div>
            </div>
            <div class="assessments">
                <div class="h1">
                    <h1>Other assessments</h1>
                </div>
                <div class="container-asses">
                    <div class="wrapper-asses">
                        <h2>Assessment</h2>
                        <p>Lorem ipsum dolor, sit amet consectetur adipisicing elit. Eius pariatur alias iusto ipsa inventore tempore.</p>
                    </div>
                    <div class="wrapper-asses">
                        <h2>Assessment</h2>
                        <p>Lorem ipsum dolor, sit amet consectetur adipisicing elit. Eius pariatur alias iusto ipsa inventore tempore.</p>
                    </div>
                    <div class="wrapper-asses">
                        <h2>Assessment</h2>
                        <p>Lorem ipsum dolor, sit amet consectetur adipisicing elit. Eius pariatur alias iusto ipsa inventore tempore.</p>
                    </div>
                </div>
            </div>
            <div className='quiz-bg' style={{display: openQuiz1 ? 'block' : 'none'}} onClick={handleBgClose}></div>
            <div className='quiz1' style={{display: openQuiz1 ? 'flex' : 'none'}}>
                <div className='close-btn' onClick={handleQuiz1Close}>
                    <ion-icon name="close"></ion-icon>
                </div>
                <p>{question}</p>

                <ul>
                    {choices.map((choice, index) => (
                        <li key={index} onClick={() => onChoiceClick(choice)}
                        style={{
                            backgroundImage:
                                selectedChoices[`choice${activeQuestion + 1}`] === choices.indexOf(choice) + 2
                                    ? 'linear-gradient(to right, #5d9add, #9294f2)'
                                    : '', color: selectedChoices[`choice${activeQuestion + 1}`] === choices.indexOf(choice) + 2
                                    ? 'white' : 'black'
                        }}  >{choice}</li>
                    ))}
                </ul>

                <button onClick={activeQuestion < questions.length - 1 ? onClickNext : onClickFinish}>{activeQuestion === questions.length - 1 ? 'Finish' : 'Next'}</button>
            </div>
        </div>
    );
}

export const quiz = {
    topic: 'Javascript',
    level: 'Beginner',
    totalQuestions: 4,
    perQuestionScore: 5,
    questions: [
      {
        question: 'Which function is used to serialize an object into a JSON string in Javascript?',
        choices: ['stringify()', 'parse()', 'convert()'],
        type: 'MCQs',
        correctAnswer: 'stringify()',
      },
      {
        question: 'Which of the following keywords is used to define a variable in Javascript?',
        choices: ['var', 'let', 'var and let'],
        type: 'MCQs',
        correctAnswer: 'var and let',
      },
      {
        question:
          'Which of the following methods can be used to display data in some form using Javascript?',
        choices: ['document.write()', 'console.log()', 'window.alert'],
        type: 'MCQs',
        correctAnswer: 'All of the above',
      },
      {
        question: 'How can a datatype be declared to be a constant type?',
        choices: ['const', 'var', 'let'],
        type: 'MCQs',
        correctAnswer: 'const',
      },
    ],
  }
