import { useState, useRef } from 'react'
import Card from './card'

export default function Cards() {
    const [cards, setCards] = useState([
        { id: 0, name: 'Bryan Cranston', status: '', img: '/img/game/01.jpg' },
        { id: 0, name: 'Bryan Cranston', status: '', img: '/img/game/01.jpg' },
        { id: 1, name: 'Bryan Cranston', status: '', img: '/img/game/02.jpg' },
        { id: 1, name: 'Bryan Cranston', status: '', img: '/img/game/02.jpg' },
        { id: 2, name: 'Bryan Cranston', status: '', img: '/img/game/03.png' },
        { id: 2, name: 'Bryan Cranston', status: '', img: '/img/game/03.png' },
        { id: 3, name: 'Bryan Cranston', status: '', img: '/img/game/04.jpg' },
        { id: 3, name: 'Bryan Cranston', status: '', img: '/img/game/04.jpg' },
        { id: 4, name: 'Bryan Cranston', status: '', img: '/img/game/05.jpg' },
        { id: 4, name: 'Bryan Cranston', status: '', img: '/img/game/05.jpg' },
        { id: 5, name: 'Bryan Cranston', status: '', img: '/img/game/06.jpg' },
        { id: 5, name: 'Bryan Cranston', status: '', img: '/img/game/06.jpg' },
        { id: 6, name: 'Bryan Cranston', status: '', img: '/img/game/07.jpg' },
        { id: 6, name: 'Bryan Cranston', status: '', img: '/img/game/07.jpg' },
        { id: 7, name: 'Bryan Cranston', status: '', img: '/img/game/08.jpg' },
        { id: 7, name: 'Bryan Cranston', status: '', img: '/img/game/08.jpg' },
    ].sort(() => Math.random() - .5))

    const [previousCardState, setPreviousCardState] = useState(-1)
    const previousIndex = useRef(-1)

    const matchCheck = (currentCard) => {
        if(cards[currentCard].id === cards[previousCardState].id){
            cards[previousCardState].status = 'active matched'
            cards[currentCard].status = 'active matched'
            setPreviousCardState(-1)
        }else{
            cards[currentCard].status = 'active'
            setCards([...cards])
            setTimeout(() => {
                setPreviousCardState(-1)
                cards[currentCard].status = 'unmatch'
                cards[previousCardState].status = 'unmatch'
                setCards([...cards])
            }, 1000);
        }
    }

    const clickhandler = (index) => {
        
        if(index !== previousIndex.current){
            if(cards[index].status === 'active matched'){
                alert('already matched')
            }else{
                if(previousCardState === -1){
                    previousIndex.current = index
                    cards[index].status = 'active'
                    setCards([...cards])
                    setPreviousCardState(index)
                }else{
                    matchCheck(index)
                    previousIndex.current = -1
                }
            }
        }else{
            alert('card currently seleted')
        }

    }

    return (
      <div className="container-game">
        { cards.map((card, index) => {
            return <Card key={index} card={card} index={index} clickhandler={clickhandler} />
        })}
      </div>
    );
}
