import React, { useState, useEffect } from 'react';
import './mind_game.css'

const MindGame = () => {
  const [gameStarted, setGameStarted] = useState(false);
  const [flippedCards, setFlippedCards] = useState(0);
  const [totalFlips, setTotalFlips] = useState(0);
  const [totalTime, setTotalTime] = useState(0);
  const [boardDimensions, setBoardDimensions] = useState(4);
  const [cards, setCards] = useState([]);

  useEffect(() => {
    generateGame();
  }, []);

  const shuffle = (array) => {
    const clonedArray = [...array];

    for (let i = clonedArray.length - 1; i > 0; i--) {
      const randomIndex = Math.floor(Math.random() * (i + 1));
      const original = clonedArray[i];

      clonedArray[i] = clonedArray[randomIndex];
      clonedArray[randomIndex] = original;
    }

    return clonedArray;
  };

  const pickRandom = (array, items) => {
    const clonedArray = [...array];
    const randomPicks = [];

    for (let i = 0; i < items; i++) {
      const randomIndex = Math.floor(Math.random() * clonedArray.length);

      randomPicks.push(clonedArray[randomIndex]);
      clonedArray.splice(randomIndex, 1);
    }

    return randomPicks;
  };

  const generateGame = () => {
    if (boardDimensions % 2 !== 0) {
      throw new Error('The dimension of the board must be an even number.');
    }

    const emojis = ['🥔', '🍒', '🥑', '🌽', '🥕', '🍇', '🍉', '🍌', '🥭', '🍍'];
    const picks = pickRandom(emojis, (boardDimensions * boardDimensions) / 2);
    const items = shuffle([...picks, ...picks]);

    setCards(items);
  };

  const startGame = () => {
    setGameStarted(true);
    setTotalFlips(0);

    const interval = setInterval(() => {
      setTotalTime((prevTime) => prevTime + 1);
    }, 1000);

    return () => clearInterval(interval);
  };

  const flipCard = (index) => {
    setFlippedCards((prevFlipped) => prevFlipped + 1);
    setTotalFlips((prevFlips) => prevFlips + 1);

    if (!gameStarted) {
      startGame();
    }

    if (flippedCards <= 1) {
      const updatedCards = [...cards];
      updatedCards[index].flipped = true;
      setCards(updatedCards);
    }

    if (flippedCards === 1) {
      const flippedIndexes = cards.reduce(
        (acc, card, i) => (card.flipped ? [...acc, i] : acc),
        []
      );

      if (cards[flippedIndexes[0]].emoji === cards[flippedIndexes[1]].emoji) {
        const updatedCards = [...cards];
        updatedCards[flippedIndexes[0]].matched = true;
        updatedCards[flippedIndexes[1]].matched = true;
        setCards(updatedCards);
      }

      setTimeout(() => {
        setFlippedCards(0);
      }, 1000);
    }
  };

  const renderCard = (card, index) => (
    <div
      key={index}
      className={`card ${card.flipped ? 'flipped' : ''} ${
        card.matched ? 'matched' : ''
      }`}
      onClick={() => flipCard(index)}
    >
      <div className="card-front"></div>
      <div className="card-back">{card.flipped ? card.emoji : ''}</div>
    </div>
  );

  return (
    <div className="game">
      <div className="controls">
        <button onClick={startGame} disabled={gameStarted}>
          Start
        </button>
        <div className="stats">
          <div className="moves">{totalFlips} moves</div>
          <div className="timer">Time: {totalTime} sec</div>
        </div>
      </div>
      <div className="board-container">
        <div className="board" style={{ gridTemplateColumns: `repeat(${boardDimensions}, auto)` }}>
          {cards.map((card, index) => renderCard(card, index))}
        </div>
        {cards.every((card) => card.matched) && (
          <div className="win">
            <span className="win-text">
              You won!<br />
              with <span className="highlight">{totalFlips}</span> moves<br />
              under <span className="highlight">{totalTime}</span> seconds
            </span>
          </div>
        )}
      </div>
    </div>
  );
};

export default MindGame;
