import '../css/Home.css';
import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';


export default function Home() {
    const [navOpen, setNavOpen] = useState(false);
    const mbNavRef = useRef(null);
    const navOpenRef = useRef(null);
  
    const [loginOpen, setLoginOpen] = useState(false);
    const [signupOpen, setSignupOpen] = useState(false);
  
  
    const handleNavOpen = () => {
      setNavOpen(!navOpen);
      document.body.style.overflow = 'hidden';
    };
    const handleNavClose = () => {
      setNavOpen(false);
      document.body.style.overflow = 'auto';
    };
  
  
    const handleLoginOpen = () => {
      setLoginOpen(true)
  
      console.log(loginOpen)
    };
    const handleLoginClose = () => {
      setLoginOpen(false)
    };
  
  
    const handleSignupOpen = () => {
      setSignupOpen(true)
    };
    const handleSignupClose = () => {
      setSignupOpen(false)
    };
  
      useEffect(() => {
      const handleClickOutside = (event) => {
          const mbNavMain = mbNavRef.current;
          const navOpenMain = navOpenRef.current;
  
          if (!mbNavMain.contains(event.target) && !navOpenMain.contains(event.target)) {
              setNavOpen(!navOpen);
          }
      };
  
      console.log(navOpen);
  
      if (navOpen === true) {
          document.body.addEventListener('click', handleClickOutside);
      }
  
      return () => {
          document.body.removeEventListener('click', handleClickOutside);
      }
      
  }, [navOpen]);


        const [users, setUsers] = useState([]);

        useEffect (() => {

            <getUser />

        }, []);


        const [registerForm, setRegisterForm] = useState({
            first_name: null,
            last_name: null,
            username: null,
            email: null,
            phone_number: null,
            gender: null,
            password: null
        });


        const [password1, setPassword1] = useState(null);
        const [password2, setPassword2] = useState(null);
        const [password, setPassword] = useState(null);

        const handlePassword1 = (event) => {
            setPassword1(event.target.value);
        };
        const handlePassword2 = (event) => {
            setPassword2(event.target.value);
        };

        let createUser = async () => {
            fetch('http://127.0.0.1:8000/api/create-user/', {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                  body: JSON.stringify(registerForm),
            })
        }

        const [email, setEmail] = useState('');
        const [phoneNumber, setPhoneNumber] = useState('');
        const [username, setUsername] = useState('');


        useEffect(() => {
            setRegisterForm((prevForm) => ({
              ...prevForm,
              password: password2,
            }));
          }, [password2]);

        const handlePasswordValidation = (event) => {
            
            event.preventDefault();

            const emailToCheck = email;
            const phoneNumberToCheck = phoneNumber;
            const usernameToCheck = username;

            const emailExists = users.map(user => user.email).includes(emailToCheck);
            const phoneNumberExists = users.map(user => user.phone_number).includes(phoneNumberToCheck);
            const usernameExists = users.map(user => user.username).includes(usernameToCheck);


            if (password2 === password1) {
                    setPassword(password2);
                    
                    console.log(password2);

                    setRegisterForm((prevForm) => ({
                        ...prevForm,
                        password: password,
                    })); 

                    
                    
                    if (emailExists){
                        console.log('email exists')
                    }
                    else if (phoneNumberExists){
                        console.log('phone number exist')
                    }
                    else if (usernameExists){
                        console.log('username exist')
                    } 
                    else{
                        console.log(registerForm)
                        createUser();
                        resetForm();
                    }
            } else{
                console.log('password dont match')
            }
        };

        const resetForm = () => {
            setRegisterForm({
              first_name: '',
              last_name: '',
              username: '',
              email: '',
              phone_number: '',
              gender: '',
              password: ''
            });

            setPassword1('');
            setPassword2('');
            setPassword('');
          };

        const handleInputChange = (event) => {
            const { name, value } = event.target;

            setRegisterForm((prevForm) => ({
              ...prevForm,
              [name]: value
            }));

            setEmail(registerForm.email);
            setPhoneNumber(registerForm.phone_number);
            setUsername(registerForm.username);
        };

        // const handleInputChange2 = (event) => {
        //     const { name, value } = event.target;

        //     const valueToCheck = value;

        //     // Use map to check if the value exists
        //     const valueExists = users.map(user => user[name]).includes(valueToCheck);

        //     // Log to the console
        //     if (valueExists) {
                
        //     } else {
        //         setRegisterForm((prevForm) => ({
        //             ...prevForm,
        //             [name]: value
        //           }));
        //         console.log('Email does not exist in the data fetched from Django API.');
        //     }
        // };

        // useEffect (() => {

            

        // }, [handleInputChange2]);

    


        // const SampleAssessment = {
        //     username: 'jonas',
        //     AnxietyScore: 'SampleAnxietyScore',
        //     IQScore: 'SampleIQScore',
        //     PersonalityScore: 'SamplePersonalityScore',
        //   };

        //   console.log(JSON.stringify(SampleAssessment));

        //   let createAssessment = async () => {
        //     fetch('http://127.0.0.1:8000/api/create/', {
        //       method: "POST",
        //       headers: {
        //           'Content-Type': 'application/json'
        //       },
        //       body: JSON.stringify(SampleAssessment),
        //     })
        //   }

        //   createAssessment();


    return (
      <div>
        <div className="bg-imgs">
            <div className="head">
                <img src= "img/header.png" />
            </div>
            <div className="center">
                <img src= "img/girl-swing.svg" />
            </div>
            <div className="foot">
                <img src= "img/footer.svg" />
            </div>
            <div className="foot-img">
                <img src="img/bot-r.svg" id = "bot-r" />
                <img src="img/bot-l.svg" id = "bot-l" />
            </div>
        </div>
  
        <nav>
            <h1>LOGO CalmSpace</h1>
            <ul>
                <Link to = "/assessment"><li className = "nav-links">Assessments</li></Link>
                <Link to = "/exercise"><li className = "nav-links">Exercises</li></Link>
                <Link to = "/resource"><li className = "nav-links">Resource Hub</li></Link>
                <Link to = "/clinic"><li className = "nav-links">Clinic</li></Link>
                <li id = "login" onClick={handleLoginOpen}>Log In</li>
            </ul>
            <div className="mob-nav" id = "mb-nav-open" ref={navOpenRef} onClick={handleNavOpen}>
                <ion-icon name="menu"></ion-icon>
            </div>
            <div className ="mb-nav-main" id = "mb-nav-main" ref={mbNavRef} style = {{display : navOpen === true ? 'flex' : 'none'}}>
                <div className ="mb-nav-head" id = "mb-nav-close" onClick={handleNavClose}>
                    <ion-icon name ="close"></ion-icon>
                </div>
                <ul>
                    <Link to = "/assesment">
                        <li className = "mb-nav-opt" id = "mb-nav-asses"><ion-icon name="document-text" className = "mb-nav-icon"></ion-icon> Assessments</li>
                    </Link>
                    <Link to = "/assesment">
                        <li className = "mb-nav-opt" id = "mb-nav-exercise"><ion-icon name="barbell" className = "mb-nav-icon"></ion-icon> Exercises</li>
                    </Link>
                    <Link to = "/assesment">
                        <li className = "mb-nav-opt" id = "mb-nav-resource"><ion-icon name="cube" className = "mb-nav-icon"></ion-icon> Resource Hub</li>
                    </Link>
                    <Link to = "/assesment">
                        <li className = "mb-nav-opt" id = "mb-nav-clinic"><ion-icon name="medkit"></ion-icon> Clinic</li>
                    </Link>
                    <a>
                        <li className = "mb-nav-opt" id = "mb-nav-login"><ion-icon name="log-in"></ion-icon> Log in</li>
                    </a>
                </ul>
            </div>
        </nav>
  
        <section className ="main-sec">
          <h1>Providing High Quality <br />Mental Health Care</h1>
          <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. 
              Eius rerum voluptate explicabo hic distinctio deleniti cum, 
              ea ut minus accusamus minima? Quasi quibusdam dignissimos quae 
              rerum explicabo animi incidunt magni!</p>
          <p id = "register" onClick={handleSignupOpen}>Register now</p>
        </section>
  
        <section className = "bg-login" id = "bg-login" style = {{display : loginOpen === false && signupOpen === false ? 'none' : 'flex'}} onClick={() => { handleLoginClose(); handleSignupClose(); }}>
  
        </section>
  
        <div className="login-frm" id= "login-frm" style = {{display : loginOpen === false ? 'none' : 'flex'}}>
          <h2>Login</h2><br />
          <form action="" method="">
              <div className="form-group">
                  <input type="text" id="username" name="username" required placeholder="Username" />
              </div>
              <div className="form-group">
                  <input type="password" id="password" name="password" required placeholder="Password" />
              </div>
              <div className="form-group" id = "form-group-a">
                  <a href="#">Forgot password</a>
                  <a href="#" id = "login-to-signup" onClick={() => { handleLoginClose(); handleSignupOpen(); }}>Sign up</a>
              </div>
              <div className="form-group">
                  <button type="submit" >Login</button>
              </div>
          </form>
          <div className="form-close" id = "login-close" onClick={handleLoginClose}>
              <ion-icon name="close"></ion-icon>
          </div>
        </div>
  
        <div className = "signup-frm" id= "signup-frm" style = {{display : signupOpen === false ? 'none' : 'flex'}}>
  
        <h2>Sign Up</h2>
          <form>
  
              <div className ="form-group" id='fn-ln'>
                  <input type="text" id="sgnUp-firstName" name="first_name" value={registerForm.first_name} required placeholder="First Name" onChange={handleInputChange}/>
  
                  <input type="text" id="sgnUp-lastName" name="last_name" value={registerForm.last_name} required placeholder="Last Name" onChange={handleInputChange}/>
              </div>
  
              <div className ="form-group">
                  <input type="text" id="sgnUp-username" name="username" value={registerForm.username} required placeholder="Username" onChange={handleInputChange}/>
              </div>
  
              <div className ="form-group">
                  <input type="email" id="sgnUp-email" name="email" value={registerForm.email} required placeholder="Email" onChange={handleInputChange}/>
              </div>
  
              <div className ="form-group">
                  <input type="tel" id="sgnUp-phoneNumber" name="phone_number" value={registerForm.phone_number} required placeholder="Phone Number" onChange={handleInputChange}/>
              </div>
  
              <div className ="form-group">
                  <input type="password" id="sgnUp-password" name="password1" value={password1} required placeholder="Password" onChange={handlePassword1}/>
              </div>
  
              <div className ="form-group">
                  <input type="password" id="sgnUp-confirmPassword" name="password2" value={password2} required placeholder="Confirm Password" onChange={handlePassword2}/>
              </div>
  
              <div className ="form-group" id="form-group-rad">
                  <label for="gender">Gender</label>
                  <div className ="radio-group">
  
                      <div className ="radio-cont">
                          <input type="radio" id="male" name="gender" value="Male" checked={registerForm.gender === 'Male'} required onChange={handleInputChange}/>
                          <label for="male">Male</label>
                      </div>
                      
                      <div className ="radio-cont">
                          <input type="radio" id="female" name="gender" value="Female" checked={registerForm.gender === 'Female'} required onChange={handleInputChange}/>
                          <label for="female">Female</label>
                      </div>
  
                      <div className ="radio-cont">
                          <input type="radio" id="other" name="gender" value="Other" checked={registerForm.gender === 'Other'} required onChange={handleInputChange}/>
                          <label for="other">Other</label>
                      </div>
  
                  </div>
  
              </div>
  
              <div className ="form-group">
                  <button type="submit" onClick={handlePasswordValidation}>Sign Up</button>
              </div>
  
              <div className="form-close" id = "signup-close"  onClick={handleSignupClose}>
                <ion-icon name="close"></ion-icon>
              </div>
          </form>
  
        </div>
      </div>
    );
  }


  
    export const getUsers = async () => {
        let response = await fetch('http://127.0.0.1:8000/api/');
        let data = await response.json();
        console.log('DATA:', data);
        setUsers(data);
    }
