import React, { useState, useEffect } from 'react';

const TimeSlots = () => {
  const [timeSlots, setTimeSlots] = useState([]);
  const [username, setUsername] = useState('current_user');

  useEffect(() => {
    // Fetch initial time slots from Django API
    fetch('http://localhost:8000/api/timeslots/')
      .then((response) => response.json())
      .then((data) => {
        console.log('Initial time slots:', data);
        setTimeSlots(data);
      });

    // Establish WebSocket connection
    const newSocket = new WebSocket('ws://localhost:8000/ws/timeslot/');
    newSocket.onopen = () => {
      console.log('WebSocket connection opened');
    };
    newSocket.onclose = (event) => {
      console.log('WebSocket connection closed:', event);
    };
    newSocket.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    // Cleanup WebSocket connection on component unmount
    return () => {
      newSocket.close();
    };
  }, []);

  const handleData = (data) => {
    const message = JSON.parse(data);
    console.log('WebSocket message:', message);

    // Refresh time slots after a real-time update
    fetch('http://localhost:8000/api/timeslots/')
      .then((response) => response.json())
      .then((data) => {
        console.log('Updated time slots:', data);
        setTimeSlots(data);
      });
  };

  const handleButtonClick = async (timeSlotId) => {
    // Send a request to update the time slot on the server
    await fetch(`http://localhost:8000/api/timeslots/${timeSlotId}/`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ user: username }),
    });
  };

  return (
    <div>
      {/* WebSocket component for real-time updates */}
      <WebSocket
        url="ws://localhost:8000/ws/timeslot/"
        onMessage={handleData}
      />

      {/* Render time slots buttons */}
      {timeSlots.map((slot) => (
        <button
          key={slot.id}
          onClick={() => handleButtonClick(slot.id)}
          disabled={slot.user !== null}
          style={{ backgroundColor: slot.user !== null ? 'red' : 'green' }}
        >
          {slot.date} - {slot.branch}
        </button>
      ))}
    </div>
  );
};

export default TimeSlots;
