import React, { useEffect, useState, useRef } from 'react';

const Clinic = () => {
    const wrapperRef = useRef(null);
    const carouselRef = useRef(null);
    const [isDragging, setIsDragging] = useState(false);
    const [isAutoPlay, setIsAutoPlay] = useState(true);

    useEffect(() => {
        const wrapper = wrapperRef.current;
        const carousel = carouselRef.current;

        if (wrapper && carousel) {
            const firstCardWidth = carousel.querySelector(".card").offsetWidth;
            const arrowBtns = wrapper.querySelectorAll(".wrapper i");
            const carouselChildrens = [...carousel.children];

            let cardPerView = Math.round(carousel.offsetWidth / firstCardWidth);

            carouselChildrens.slice(-cardPerView).reverse().forEach(card => {
                carousel.insertAdjacentHTML("afterbegin", card.outerHTML);
            });

            carouselChildrens.slice(0, cardPerView).forEach(card => {
                carousel.insertAdjacentHTML("beforeend", card.outerHTML);
            });

            carousel.classList.add("no-transition");
            carousel.scrollLeft = carousel.offsetWidth;
            carousel.classList.remove("no-transition");

            arrowBtns.forEach(btn => {
                btn.addEventListener("click", () => {
                    carousel.scrollLeft += btn.id == "left" ? -firstCardWidth : firstCardWidth;
                });
            });
        }
    }, [isAutoPlay, isDragging]);

    return (
        <>
            <div className="container hero">
                {/* Your hero content goes here */}
            </div>
            <div className="container tutorial">
                {/* Your tutorial content goes here */}
            </div>
            <div className="container services">
                <div className="wrapper services" ref={wrapperRef}>
                    <div className="services-text">
                        <p>WHAT WE <span>PROVIDE</span></p>
                    </div>
                    <div className="services-img" ref={carouselRef}>
                        <i id="left" className="fa-solid fa-angle-left">
                            <ion-icon name="chevron-back-outline" className="i-icon"></ion-icon>
                        </i>
                        <ul className="carousel">
                            <li className="card">
                                <div className="img"><img src="img/mental-health.png" alt="Mental Health" /></div>
                                <h2>Adolescent/Adult Psychiatry</h2>
                            </li>
                            {/* Add other card items here */}
                        </ul>
                        <i id="right" className="fa-solid fa-angle-right">
                            <ion-icon name="chevron-forward-outline" className="i-icon"></ion-icon>
                        </i>
                    </div>
                </div>
            </div>
            <div className="container appoint">
                {/* Your appointment content goes here */}
            </div>
            <div className="container aboutUs">
                {/* Your about us content goes here */}
            </div>
            <div className="container comments">
                {/* Your comments content goes here */}
            </div>
        </>
    );
};

export default Clinic;
