import React, { useState } from 'react';
import './YourComponent.css';  

const BreathingComponent = () => {
  const [breathsRemaining, setBreathsRemaining] = useState(3);

  const handleBeginClick = () => {
    // Add your logic to start the breathing process
    // For example, you could start a timer here
  };

  return (
    <div>
      <div className="input">
        <label>Select Breaths</label>
        <select className="breath-input">
          <option value="3">3 Breaths</option>
          <option value="5">5 Breaths</option>
          <option value="7">7 Breaths</option>
        </select>
      </div>
      <div className="circle-wrap">
        <div className="circle-outline"></div>
        <div className="circle-progress"></div>
      </div>
      <p className="breaths">Breaths remaining: <span className="breaths-text">{breathsRemaining}</span></p>
      <p className="instructions">Are you ready to start breathing?</p>
      <button className="start" onClick={handleBeginClick}>Begin</button>
    </div>
  );
};

export default BreathingComponent;
