import React, { useState, useEffect } from 'react';
import '../css/BreathingExercise.css';

function BreathingExercise() {
  const [text, setText] = useState('');
  const [containerClass, setContainerClass] = useState('container');
  const [pointerClass, setPointerClass] = useState('pointer-container');

  const totalTime = 7500;
  const breatheTime = (totalTime / 5) * 2;
  const holdTime = totalTime / 5;

  const breathAnimation = () => {
    setText('Breathe In!');
    setContainerClass('container grow');
    setPointerClass('pointer-container rotate');

    setTimeout(() => {
      setText('Hold');

      setTimeout(() => {
        setText('Breathe Out!');
        setContainerClass('container shrink');
      }, holdTime);
    }, breatheTime);
  };

  useEffect(() => {
    const interval = setInterval(breathAnimation, totalTime);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="breathing-app">
      <h1>Relaxer</h1>
      <button onClick={breathAnimation}>start</button>
      <div className={containerClass} id="container">
        <div className="circle"></div>
        <p id="text">{text}</p>
        <div className={pointerClass} id="pointer-container">
          <span className="pointer"></span>
        </div>
        <div className="gradient-circle"></div>
      </div>
    </div>
  );
}

export default BreathingExercise;
