import React, { useState, useEffect } from 'react';
import '../css/BreathingExercise.css';

function BreathingExercise() {
  const [breathsLeft, setBreathsLeft] = useState(3);
  const [instructionsText, setInstructionsText] = useState('Are you ready to start breathing?');
  const [isBreathingStarted, setIsBreathingStarted] = useState(false);

  const handleBreathInputChange = (event) => {
    setBreathsLeft(event.target.value);
  };

  const handleStartClick = () => {
    setIsBreathingStarted(true);
    setInstructionsText('Get relaxed, and ready to begin breathing');

    const breathingInterval = setInterval(() => {
      growCircle();
      breathTextUpdate();
    }, 12000);

    setTimeout(() => {
      setInstructionsText('Breathing is about to begin...');
    }, 2000);

    setTimeout(() => {
      growCircle();
      breathTextUpdate();
    }, 4000);
  };

  const growCircle = () => {
    const circleProgress = document.querySelector('.circle-progress');
    circleProgress.classList.add('circle-grow');
    setTimeout(() => {
      circleProgress.classList.remove('circle-grow');
    }, 8000);
  };

  const breathTextUpdate = () => {
    setBreathsLeft((prevBreathsLeft) => prevBreathsLeft - 1);

    if (breathsLeft === 0) {
      clearInterval(breathingInterval);
      setInstructionsText('Breathing session completed. Click "Begin" to start another session!');
      setIsBreathingStarted(false);
    } else {
      setInstructionsText(breathsLeft === 1 ? 'Breath in' : 'Hold Breath');
      setTimeout(() => {
        setInstructionsText('Exhale Breath Slowly');
      }, 4000);
    }
  };

  return (
    <div className="app">
      <div className="input">
        <label>Select Breaths</label>
        <select className="breath-input" value={breathsLeft} onChange={handleBreathInputChange}>
          <option value="3">3 Breaths</option>
          <option value="5">5 Breaths</option>
          <option value="7">7 Breaths</option>
        </select>
      </div>
      <div className="circle-wrap">
        <div className="circle-outline"></div>
        <div className="circle-progress"></div>
      </div>
      <p className="breaths">Breaths remaining: <span className="breaths-text">{breathsLeft}</span></p>
      <p className="instructions">{instructionsText}</p>
      <button className="start" onClick={handleStartClick} disabled={isBreathingStarted}>
        {isBreathingStarted ? 'Breathing in Progress' : 'Begin'}
      </button>
    </div>
  );
}

export default BreathingExercise;
