import '../css/nav.css';

import React, { useState, useRef, useEffect, useContext } from 'react';
import Nav from './nav';
import AuthContext from '../context/AuthContext';
import '../css/profile.css';


export default function Profile() {
    return (
        <body>
            <Nav color = 'white' page = 'profile' rIcon = 'white'/>
            <Nav_child />
        </body>
    );
}

function Nav_child() {

    let {user, authTokens} = useContext(AuthContext);

    const [isEditable, setIsEditable] = useState(false);

    const [formFields, setFormFields] = useState({
        firstname: user.first_name,
        lastname: user.last_name,
        username: user.username,
        contact: user.phone_number,
        email: user.email,
      });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormFields((prevFields) => ({
          ...prevFields,
          [name]: value,
        }));
    };

    const [passwordFields, setPasswordFields] = useState({
        oldPassword: '',
        newPassword: '',
        confirmPassword: '',
    });

    const handlePasswordChange = (e) => {
        const { name, value } = e.target;
        setPasswordFields((prevFields) => ({
          ...prevFields,
          [name]: value,
        }));
      };

      const [passwordError, setPasswordError] = useState({
        isVisible: false,
        message: '',
    });

    function verifyPassword(newPassword, confirmPassword) {
        // Password requirements
        const minLength = 8;
        const hasSpecialChar = /[!@#$%^&*(),.?":{}|<>]/.test(newPassword);
        const hasNumber = /\d/.test(newPassword);

        // Check if the new password meets the requirements
        if (
            newPassword.length >= minLength &&
            hasSpecialChar &&
            hasNumber 
            // newPassword !== user.password
        ) {
            // Check if the new password matches the confirmed password
            return newPassword === confirmPassword;
        } else {
            // Set the specific error message
            setPasswordError({
                isVisible: true,
                message: 'Invalid password. Please check the requirements and try again.',
            });
        }

        return false;
    }

    const handleSaveChanges = () => {
        // Verify the new passwords
        const isPasswordValid = verifyPassword(
            passwordFields.newPassword,
            passwordFields.confirmPassword
        );

        if (isPasswordValid) {
            // Perform save changes logic
            // ...

            // Reset the password error state
            setPasswordError({
                isVisible: false,
                message: '',
            });
        } else {
            // Display the password error popup
            // The popup should be an HTML div styled with CSS
            // that you conditionally render based on passwordError.isVisible
        }
    };

    console.log(passwordFields)

    return(
        <div class="profile">
            <div class="profile-h1">
                <p>Profile</p>
            </div>
            <div class="main-profile">
                <div class="general-info">
                    <div class="gen-info-wrapper">
                        <div class="profile-form-row">
                            <div class="profile-form-group row">
                                <label for="firstname">Firstname</label>
                                <input type="text" id="firstname" name="firstname" value={formFields.firstname} onChange={handleInputChange} disabled={!isEditable}/>
                            </div>
                            <div class="profile-form-group row">
                                <label for="lastname">Lastname</label>
                                <input type="text" id="lastname" name="lastname" value={formFields.lastname} onChange={handleInputChange} disabled={!isEditable}/>
                            </div>
                        </div>
                        <div class="profile-form-row">
                            <div class="profile-form-group row">
                                <label for="text">Username</label>
                                <input type="text" id="username" name="username" value={formFields.username} onChange={handleInputChange} disabled={!isEditable}/>
                            </div>
                            <div class="profile-form-group row">
                                <label for="contact">Contact number</label>
                                <input type="tel" id="contact" name="contact" value={formFields.contact} onChange={handleInputChange} disabled={!isEditable}/>
                            </div>
                        </div>
                        <div class="profile-form-group">
                            <label for="email">Email</label>
                            <input type="email" id="email" name="email" value={formFields.email} onChange={handleInputChange} disabled={!isEditable}/>
                        </div>
                    </div>
                </div>
                <div class="password-change">
                    <div class="pw-chng-form">
                    <div class="profile-form-group-pw">
                        <label for="inputPassword4">Old Password</label>
                        <input
                        type="password"
                        class="form-control"
                        id="inputPassword4"
                        name="oldPassword"
                        value={passwordFields.oldPassword}
                        onChange={handlePasswordChange}
                        />
                    </div>
                    <div class="profile-form-group-pw">
                        <label for="inputPassword5">New Password</label>
                        <input
                        type="password"
                        class="form-control"
                        id="inputPassword5"
                        name="newPassword"
                        value={passwordFields.newPassword}
                        onChange={handlePasswordChange}
                        />
                    </div>
                    <div class="profile-form-group-pw">
                        <label for="inputPassword6">Confirm Password</label>
                        <input
                        type="password"
                        class="form-control"
                        id="inputPassword6"
                        name="confirmPassword"
                        value={passwordFields.confirmPassword}
                        onChange={handlePasswordChange}
                        />
                    </div>
                    </div>
                    <div class="pw-chng-texts">
                        <p class="small-text-head">Password requirements</p>
                        <p class="small-text">To create a new password, you have to meet all of the following requirements:</p>
                        <ul class="small-text">
                            <li>Minimum 8 character</li>
                            <li>At least one special character</li>
                            <li>At least one number</li>
                            <li>Canâ€™t be the same as a previous password</li>
                        </ul>
                    </div>
                </div>  
                <div class="profile-actions">
                    <button onClick={handleSaveChanges}>Save Changes</button>
                    <a href = "#">Log out</a>
                </div>
            </div>
            {passwordError.isVisible && (
            <div className="password-error-popup">
                <p>{passwordError.message}</p>
                <button onClick={() => setPasswordError({ isVisible: false, message: '' })}>
                    Close
                </button>
            </div>
            )}
        </div>
    );
};
