import React, { useState, useRef, useEffect, useContext } from 'react';
import '../css/resource.css';
import Nav from './nav';
import AuthContext from '../context/AuthContext';



export default function Resource() {
    return (
        <body>
            <Nav color = 'black' page = 'resource' rIcon = 'black' absolute = 'true'/>
            <Resource_main />
        </body>
    );
}

function Resource_main (){

    const [mobileFilter, setMobileFilter] = useState(false);

    const filterOpen = () => {
        setMobileFilter(!mobileFilter);
        console.log(mobileFilter);
    };

    const filterClose = () => {
        setMobileFilter(false);
    };

    useEffect (() => {
        
        const handleWindowResize = () => {
            const windowWidth = window.innerWidth;
            if (windowWidth >= 1020) {
                setMobileFilter(false);
              }
            };
          
            window.addEventListener('resize', handleWindowResize);

        return () => {
            window.removeEventListener('resize', handleWindowResize);
        };

    }, [mobileFilter]);

    // filterOpt-all

    const [filter_all, setFilter_all] = useState(true);
    const [filter_news, setFilter_news] = useState(false);
    const [filter_video, setFilter_video] = useState(false);
    const [filter_article, setFilter_article] = useState(false);

    const selectFilterAll = () => {
        setFilter_all(true);
        setFilter_news(false);
        setFilter_video(false);
        setFilter_article(false);
    };

    const selectFilterArticle = () => {
        setFilter_all(false);
        setFilter_news(false);
        setFilter_video(false);
        setFilter_article(true);
    };

    const selectFilterNews = () => {
        setFilter_all(false);
        setFilter_news(true);
        setFilter_video(false);
        setFilter_article(false);
    };

    const selectFilterVideo = () => {
        setFilter_all(false);
        setFilter_news(false);
        setFilter_video(true);
        setFilter_article(false);
    };

    let {resources} = useContext(AuthContext);

    const [searchQuery, setSearchQuery] = useState('');

    const handleSearchInputChange = (event) => {
        setSearchQuery(event.target.value);
    };

    console.log(searchQuery)

    return(
        <div>
            <div class="hero-rs">
        <div class="hero-cntxt-rs">
            <h1>Lorem, ipsum dolor.</h1>
            <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolorum asperiores minus ipsa consequatur molestiae fugiat dolor maxime quaerat, nemo, porro, ex ea nulla! Adipisci, impedit. Pariatur aut est cupiditate facilis.</p>
        </div>
    </div>

    <div class="resource-main">
        {/* <!--search bar--> */}
        <div class="search-bar">
            <input type = "text"  name="search-bar" placeholder="Search" onChange={handleSearchInputChange}/>
            <a href = "#" class="srch-icon">
                <ion-icon name="search"></ion-icon>
            </a>
        </div>
        {/* <!--filter--> */}
        <div class="result-main-container">
            <div class="result-filter">
                <div class="filter-choice">
                    <h3>Filter</h3>
                </div>

                {/* <!--mobile filter options--> */}
                <div class="filter-choice mobile">
                    <h3>Filter</h3>
                    <ion-icon name="chevron-down" id = "fltr-chc-open" onClick={filterOpen} style = {{display: mobileFilter === true ? 'none' : 'flex'}}></ion-icon>
                    <ion-icon name="chevron-up" id = "fltr-chc-close" onClick={filterClose} style = {{display: mobileFilter === true ? 'flex' : 'none'}}></ion-icon>
                </div>

                <a hrel = "#" class = "filter-opt mobile" id = "filterOpt-all-mb" style = {{display: mobileFilter === true ? 'flex' : 'none',
                backgroundColor: filter_all === true? 'lightgray' : '' }} onClick={selectFilterAll}>
                    <div class="filter-opt-visual">
                        <ion-icon name="albums"></ion-icon>
                        <p class = "filter-opt-text">All</p>
                    </div>
                    <p class = "filter-opt-count">12</p>
                </a>

                <a hrel = "#" class = "filter-opt mobile" id = "filterOpt-article-mb" style = {{display: mobileFilter === true ? 'flex' : 'none',
                backgroundColor: filter_article === true? 'lightgray' : ''  }} onClick={selectFilterArticle}>
                    <div class="filter-opt-visual">
                        <ion-icon name="newspaper"></ion-icon>
                        <p class = "filter-opt-text">Articles</p>
                    </div>
                    <p class = "filter-opt-count">12</p>
                </a>

                <a hrel = "#" class = "filter-opt mobile" id = "filterOpt-news-mb" style = {{display: mobileFilter === true ? 'flex' : 'none',
                backgroundColor: filter_news === true? 'lightgray' : ''  }} onClick={selectFilterNews}>
                    <div class="filter-opt-visual">
                        <ion-icon name="globe"></ion-icon>
                        <p class = "filter-opt-text">News</p>
                    </div>
                    <p class = "filter-opt-count">12</p>
                </a>

                <a hrel = "#" class = "filter-opt mobile" id = "filterOpt-video-mb" style = {{display: mobileFilter === true ? 'flex' : 'none',
                backgroundColor: filter_video === true? 'lightgray' : ''  }} onClick={selectFilterVideo}>
                    <div class="filter-opt-visual">
                        <ion-icon name="videocam"></ion-icon>
                        <p class = "filter-opt-text">Videos</p>
                    </div>
                    <p class = "filter-opt-count">12</p>
                </a>

                {/* <!--desktop filter options--> */}

                <a hrel = "#" class = "filter-opt" id = "filterOpt-all" onClick={selectFilterAll} 
                style={{backgroundColor: filter_all === true? 'lightgray' : ''}}>
                    <div class="filter-opt-visual">
                        <ion-icon name="albums"></ion-icon>
                        <p class = "filter-opt-text">All</p>
                    </div>
                    <p class = "filter-opt-count">12</p>
                </a>
                <a hrel = "#" class = "filter-opt" id = "filterOpt-article" onClick={selectFilterArticle}
                style={{backgroundColor: filter_article === true? 'lightgray' : ''}}>
                    <div class="filter-opt-visual">
                        <ion-icon name="newspaper"></ion-icon>
                        <p class = "filter-opt-text">Articles</p>
                    </div>
                    <p class = "filter-opt-count">12</p>
                    
                </a>
                <a hrel = "#" class = "filter-opt" id = "filterOpt-news" onClick={selectFilterNews}
                style={{backgroundColor: filter_news === true? 'lightgray' : ''}}>
                    <div class="filter-opt-visual">
                        <ion-icon name="globe"></ion-icon>
                        <p class = "filter-opt-text">News</p>
                    </div>
                    <p class = "filter-opt-count">12</p>
                </a>
                <a hrel = "#" class = "filter-opt" id = "filterOpt-video" onClick={selectFilterVideo}
                style={{backgroundColor: filter_video === true? 'lightgray' : ''}}>
                    <div class="filter-opt-visual">
                        <ion-icon name="videocam"></ion-icon>
                        <p class = "filter-opt-text">Videos</p>
                    </div>
                    <p class = "filter-opt-count">12</p>
                </a>

                {/* <!--script for filter options--> */}

            </div>

            {/* <!--results/ list of results--> */}

            <div class="result-container">x
                {resources.news && resources.news.map((resource, index) => (
                    <div key={index} className="result-wrapper" style={{display: filter_news ? 'flex' : filter_all ? 'flex' : 'none'}}>
                        <div className="result-img">
                            <img src={resource.image} alt={`Image for ${resource.title}`} />
                        </div>
                        <div className="result-info">
                            <h3>{resource.title}</h3>
                            <p className="result-type">{resource.resource_type}</p>
                            <p className="result-text">{resource.description}</p>
                            <a href={resource.link} target="_blank" className="button">Let's go</a>
                        </div>
                    </div>
                ))}
                {resources.article && resources.article.map((resource, index) => (
                    <div key={index} className="result-wrapper" style={{display: filter_article ? 'flex' : filter_all ? 'flex' : 'none'}}>
                        <div className="result-img">
                            <img src={resource.image} alt={`Image for ${resource.title}`} />
                        </div>
                        <div className="result-info">
                            <h3>{resource.title}</h3>
                            <p className="result-type">{resource.resource_type}</p>
                            <p className="result-text">{resource.description}</p>
                            <a href={resource.link} target="_blank" className="button">Let's go</a>
                        </div>
                    </div>
                ))}
                {resources.video && resources.video.map((resource, index) => (
                    <div key={index} className="result-wrapper" style={{display: filter_video ? 'flex' : filter_all ? 'flex' : 'none'}}>
                        <div className="result-img">
                            <img src={resource.image} alt={`Image for ${resource.title}`} />
                        </div>
                        <div className="result-info">
                            <h3>{resource.title}</h3>
                            <p className="result-type">{resource.resource_type}</p>
                            <p className="result-text">{resource.description}</p>
                            <a href={resource.link} target="_blank" className="button">Let's go</a>
                        </div>
                    </div>
                ))}
            </div>

        </div>
    </div>
        </div>
    );
}
